$(document).ready(function(){
  $(".category-picker").click(function() {
    let value = $(this).text().toLowerCase();
    let menu = value;
    switch (value) {
      case "lamper":
        menu = "lamper"
        value = "lamps"
        break;
      case "stoler":
        menu = "stoler"
        value = "chairs"
        break;
      case "klaer":
        value = "clothes"
        menu = "klaer"
      break;
      case "andre":
        value = "other"
        menu = "andre"
      break;
      case "alle":
        value = "product"
        menu = "Alle"
      break;
      case "all":
        menu = "All"
        value = "product"
      break;
    }
    $("#dropdownMenuLink").text(menu);
    if (value == "product") {
      $(".product").fadeIn()
    } else {
      $(".product").toggle(false)
      $("."+value).filter(function() {
      $(this).fadeIn()
      });
    }
    return value
  });
});
